/* ==UserStyle==
@name           gitlab.com
@namespace      github.com/openstyles/stylus
@version        1.0.0
@description    A new userstyle
@author         Me
==/UserStyle== */

@-moz-document url-prefix("https://gitlab.com/") {
    :root, html, body {
	    font-size: 16px;
        --gl-background-color-default: black;
        --gl-button-default-primary-background-color-default: black;
        --gl-button-selected-background-color-default: var(--gl-button-confirm-primary-background-color-hover);
        --gl-background-color-subtle: black;
        --gl-color-neutral-800: black;
        --gl-background-color-section: black;
	    --gray-10: black;
        --gl-background-color-strong: #111;
    }

    * {
        font-family: "MxPlus IBM VGA 8x16" !important;
        font-size: 1rem !important;
    }

    .code * {
        font-size: 1rem !important;
    }

    .top-bar-fixed, .row-content-block, .merge-request, .file-title, .well-segment, .file-row-header, .file-row, .code.monokai, .diff-line-num, .line_content, .diff-line-expand-button, .tree-item, .gl-new-dropdown-inner {
        background: black !important;
    }

    .gl-new-dropdown-inner {
        border-radius: 10px;
    }

    .new {
        background: rgba(166,226,46,.1) !important;
    }

    .old {
        background: rgba(254,147,140,.15) !important;
    }
}

