/* ==UserStyle==
@name           lccctech-hub.atlassian.net/jira/software/c/projects/ZRO/boards/74
@namespace      github.com/openstyles/stylus
@version        1.0.0
@description    A new userstyle
@author         Me
==/UserStyle== */
@-moz-document url-prefix("https://lccctech-hub.atlassian.net/") {
    :root,
    html,
    body,
    * {
        font-size: 16px;
        --gl-background-color-default: black;
        --ds-surface: black;
        --ds-surface-overlay: #000c;
        --ds-surface-sunken: black;
        --ds-surface-raised: black;
        --colorNeutralBackground1: black;
        --colorNeutralBackground: black;
        --colorDefaultBackground7: black;
        --themeBackgroundColor: black;
        --backgroundCanvas: black;
    }

    * {
        font-family: "MxPlus IBM VGA 8x16" !important;
        color: white !important;
    }

    *:not(h1, h2, h3, h4, h1 *) {
        font-size: 1rem !important;
    }

    .code * {
        font-size: 1rem !important;
    }

    .top-bar-fixed,
    .row-content-block,
    .merge-request,
    header[role="banner"],
    header button,
    div[data-tid="message-pane-layout"],
    #jira-issue-header,
    #jira-issue-header-actions,
    input,
    div[data-tid="app-layout-area--header"] * {
        background: black !important;
    }

    .ui-tree__title {
        height: unset !important;
    }

    *[data-testid="platform-board-kit.ui.card.card"]:hover {
        outline: 1px solid #888;
        border-radius: 4px;
    }

    *[data-testid="software-backlog.card-list.card.card-contents.interaction-layer.accessible-card"]:hover {
        outline: 1px solid #888;
        z-index: 100;
        border-radius: 4px;
    }

    section, .akEditor, .show-keyline, #jira-issue-header, button[data-testid="canned-comments.common.ui.comment-text-area-placeholder.textarea"] {
        border-radius: 10px !important;
    }

    img[data-testid="atlassian-navigation--product-home--icon"], img[data-testid="atlassian-navigation--product-home--logo"] {
        filter: invert(100%);;
    }
}

